.sketch::before {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  inset: 0;
  background: linear-gradient(185deg, rgba(239, 228, 200, 0.950017507) 0%, rgba(239, 228, 200, 0.1) 25%, rgba(239, 228, 200, 0.1488970588) 70%, #efe4c8 100%);
}

.sketch::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  inset: 0;
  background: linear-gradient(90deg, rgba(239, 228, 200, 0) 0%, rgba(239, 228, 200, 0) 85%, rgba(239, 228, 200, 0.9023984594) 100%);
}

