            align-content: end;
            height: 720px;
            max-height: 85vh;
            max-width: 720px;
            width: 50%;
        }
    }

    .textbox {
        position: relative;
        bottom: 0;
        align-self: center;
    }
}

@include desktop {
    .hero {
        &__image {
            height: 85vh;
            max-height: 900px;
            max-width: none;
