    &__box {
        position: relative;
        background-color: color(darksands);
        padding: $space-m;
        max-width: 500px;
        width: 85%;
        z-index: 10;

        &--one {
            bottom: 100px;
            margin-left: auto;
        }

        &--two {
            bottom: 110px;
            margin-right: auto;
        }
    }

    &__content {
        padding-bottom: $space-xs;
    }

    &__title {
        text-transform: uppercase;
        font-weight: bold;
        padding-bottom: $space-s;
        font-family: $font-secondary;
        @include fluid-font (3vw, 36px, 42px, 28px);
    }
    
    &__image {
        position: relative;
        object-fit: cover;
        height: 400px;
        width: 85%;
        
        &--one {
            object-position: -125px;
        }

        &--two {
            margin-left: auto;
            bottom: 130px;
            object-fit: cover;
        }
    }
}

@include tablet {

    .place {

        &__box {
            padding: $space-m $space-l;
        }

        &__image {
            height: 600px;
            width: 75%;
            max-width: 600px;
        }
    }
}

@include ipad-pro {

    .place {

        &__box {
            max-width: 550px;
            padding: $space-l $space-xl;
    
            &--one {
                bottom: 150px;
                transform: translateX(-100px);
            }
    
            &--two {
                bottom: 50px;
                margin-left: 0;
                transform: translateX(100px);
            }
        }

        &__image {
            max-width: 600px;
    
            &--two {
                margin-left: auto;
            }
        }
    }
}

@include laptop {
    .place {
        margin: 0 $space-xl;
    }
}

@include desktop {
    .place {
        margin: 0 $space-xxxl;
    }
}

sources.sass\\_components\\_sketch.scss:
@use '../_abstracts/index' as *;

.sketch {
    position: absolute;
    background-image: url('/img/szkic-20.jpg');
    background-size: cover;
    background-position: top left;
    width: 100%;
    height: 400px;
    bottom: 30px;
    opacity: 25%;
    mix-blend-mode: darken;

    &::before {
        content: '';
        position: absolute;
        width: 100%;
        height: 100%;
        inset: 0;
        background: linear-gradient(185deg, rgba(239,228,200,0.9500175070028011) 0%, rgba(239,228,200,0.1) 25%, rgba(239,228,200,0.14889705882352944) 70%, rgba(239,228,200,1) 100%);
    }
    &::after {
        content: '';
        position: absolute;
        width: 100%;
        height: 100%;
        inset: 0;
        background: linear-gradient(90deg, rgba(239,228,200,0) 0%, rgba(239,228,200,0) 85%, rgba(239,228,200,0.9023984593837535) 100%);
    }
}

@include tablet {
    .sketch {
        width: 85%;
        bottom: 0;
        height: 400px;
    }
}

@include ipad-pro {
    .sketch {
        width: 80%;
        height: 550px;
    }
}

@include desktop {
    .sketch {
        width: 65%;
        bottom: 50px;
    }
}

