sources.sass\\_layouts\\_form.scss:
@use '../_abstracts/index' as *;

.status {
    position: absolute;
    bottom: 16px;
    padding: 0 $space-xs;
    background-color: color(sands);
    color:color(darks);
    transition: opacity .2s;
}

.show {
    background-color: color(lights);
}

.form {
    position: relative;
    display: flex;
    flex-direction: column;
    padding: $space-xl 0 $space-xxxl 0;

    &__input {
        @include fluid-font (1vw, 16px, 16px, 16px);
        height: 40px;
        width: 90%;
        min-width: 250px;
        max-height: 250px;
        padding: $space-xxs;
        margin: $space-xxs 0;
        border: none;
        border-bottom: 2px solid color(golds);
        font-family: inherit;
        background-color: color(lights);
        resize: vertical;

        &:valid + span::before {
            content: 'âœ“';
            position: relative;
            margin-left: $space-xs;
            font-size: 18px;
            color: color(greens);
        }

        &:focus {
            border-bottom: none;
            outline-color: color(golds);
            background-color: color(whites);
        }
    }

    &__title {
        margin-bottom: $space-l;
        text-align: center;
    }

    &__info {
        position: relative;
        padding: $space-m ;
        margin: 0 auto;
        background-color: color(darksands);
        transition: background-color .3s;
        isolation: isolate;
    }

    &__info-icon {
        position: absolute;
        top: 10px;
        left: 20px;
        font-size: 48px;
        color: color(sands);
        z-index: -1;
    }

    &__disclaimer {
        width: 100%;
        padding: $space-xs 0;
        line-height: 1.4;
        @include fluid-font (1vw, 12px, 13px, 12px);
    }

    &__label {
        cursor: pointer;
    }

    &__question {
        overflow: hidden;
    }

    &__submit {
        width: 150px;
        height: 45px;
        margin: $space-s 0;
        border: none;
        letter-spacing: 2px;
        font-family: $font-primary;
        text-transform: uppercase;
        text-decoration: none;
        color: color(whites);
        background-color: color(darks);
        transition: background-color .3s, color .3s;
        cursor: pointer;

        &:focus {
            outline-color: color(golds);
        }

        &[value] {
            text-transform: uppercase;
            font-weight: bold;
            letter-spacing: 2px;
        }
    }
}

@media (hover: hover) {
    .form {
        &__submit {
            &:hover {
                @include btn-color(color(darkgolds), color(lights));
            }
        }
    }
}

@include tablet {
    .form {

        &__submit {
            width: 165px;
        }

        &__info {
            width: 75%;
        }

        &__input {
            width: 100%;

            &:valid + span::before {
                position: absolute;
                padding-top: 10px;
                left: -31px;
                font-size: 20px;
            }
        }
    }
}

@include laptop {
    .form {

        &__info {
            padding: $space-xxl ;
            margin-top: $space-xxl;
            margin-bottom: $space-s;
            width: 60%;
        }
        &__input {
            &:valid + span::before {
                left: -45px;
                font-size: 22px;
            }
        }
    }
}

sources.sass\\_layouts\\_grid.scss:
@use '../_abstracts/index' as *;

.grid {
    &__container {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        grid-template-rows: repeat (6, 1fr );
        place-items: center;
        gap: 2px;
        padding: 2px;
    }
}

.image {
    position: relative;
    width: 100%;
    height: 100%;
    min-height: 100px;
    object-fit: cover;
    object-position: center;
    transition: transform 1.5s, filter 1s;
    
    &:hover {
        transform: scale(1.05);
        filter: brightness(110%);
    }

    &__container {
        overflow: hidden;
        width: 100%;
        height: 100%;
    }
}

#one {
    grid-column: 1 / 2;
    grid-row: 1;
}

#two {
    grid-column: 2 / 3;
    grid-row: 1;
}

#three {
    grid-column: 1 / 2;
    grid-row: 2;
}

#four {
    grid-column: 2 / 3;
    grid-row: 2;
}

#five {
    grid-column: 1 / 3;
    grid-row: 3;
}

#six {
    grid-column: 1 / 2;
    grid-row: 4;
}

#seven {
    grid-column: 2 / 3;
    grid-row: 4;
}

#eight {
    grid-column: 1 / 3;
    grid-row: 5;
}

@include tablet {
    .grid {
        &__container {
            grid-template-columns: repeat(3, 1fr);
            grid-template-rows: repeat (7, 1fr);
            gap: 8px;
            padding: 8px;
        }
    }

    #one {
        grid-column: 1 / 2;
        grid-row: 1;
    }

    #two {
        grid-column: 2 / 3;
        grid-row: 1;
    }

    #three {
        grid-column: 3 / 4;
        grid-row: 1;
    }

    #four {
        grid-column: 1 / 2;
        grid-row: 2 / 3;
    }

    #five {
        grid-column: 2 / 4;
        grid-row: 2 / 3;
    }

    #six {
        grid-column: 1 / 2;
        grid-row: 3;
    }

    #seven {
        grid-column: 2 / 3;
        grid-row: 3;
    }

    #eight {
        grid-column: 3 / 4;
        grid-row: 3;
    }
}

sources.sass\\_layouts\\_showcase.scss:
@use '../_abstracts/index' as *;

.showcase {
    display: flex;
    flex-direction: column;

    &__image-container {
        padding-bottom: $space-l;
    }

    &__image-wrapper {
        position: relative;
    }

    &__image {
        object-position: center;
        object-fit: cover;
        width: 100%;
    }

    &__image-decoration {
        display: none;
        position: absolute;
        top: -40px;
        left: -40px;
        width: 100%;
        height: 100%;
        background-color: color(darksands);
        z-index: -1;
    }

    &__title {
        position: relative;
        @include fluid-font (4.5vw, 36px, 42px, 42px);
        font-family: $font-secondary;
        text-transform: uppercase;
        font-weight: normal;
    }

    &__info {
        padding-top: $space-m;
    }

    &__icons {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 16px;
        padding-top: $space-m;
    }

    &__icons-container {
        display: flex;
        flex-direction: column;
    }

    &__icon {
        margin: 0 auto;
        font-size: 36px;
        margin-top: $space-s;
    }

    &__icon-description {
        @include fluid-font (1.5vw, 16px, 20px, 20px);
        display: inline-block;
        padding: $space-xs 0;
        text-align: center;
        text-transform: capitalize;
    }
}

@include ipad-pro {
    .showcase {
        flex-direction: row;
        padding-top: $space-l;

        &__image-container {
            flex: 1;
            margin-top: $space-s;
            padding: 0 $space-m;
        }

        &__content {
            flex: 1;
            padding: 0 $space-m;
        }

        &__image {
            height: 650px;
        }

        &__image-decoration {
            display: block;
        }

        &__info {
            padding-top: $space-xl;
        }

        &__icons {
            margin-top: $space-xl;
            gap: 36px;
        }

        &__icon {
            font-size: 48px;
        }
    }
}

@include laptop {
    .showcase {
        &__image-container {
            padding: 0 $space-xxl;
        }
        &__content {
            padding: 0 $space-xxl;
        }
    }
}

